import * as React from 'react';
import {generatedata} from './generatedata'
import JqxButton from 'jqwidgets-scripts/jqwidgets-react-tsx/jqxbuttons';
import JqxGrid, { IGridProps, jqx } from 'jqwidgets-scripts/jqwidgets-react-tsx/jqxgrid';
import JqxTabs from 'jqwidgets-scripts/jqwidgets-react-tsx/jqxtabs';


class App extends React.PureComponent<{}, IGridProps> {

    private myTabs = React.createRef<JqxTabs>();
    private myGrid = React.createRef<JqxGrid>();

    constructor(props: {}) {
        super(props);
        this.excelBtnOnClick = this.excelBtnOnClick.bind(this);

        const source: any = {
            datafields: [
                { name: 'firstname', type: 'string' },
                { name: 'lastname', type: 'string' },

            ],
            datatype: 'array',
            localdata: generatedata(100)
        };
        this.state = {
            columns: [
                { text: 'First Name', datafield: 'firstname', width: 120 },
                { text: 'Last Name', datafield: 'lastname', width: 120 },

            ],
            source: new jqx.dataAdapter(source)
        }
    }
    public render() {
        return (
            <JqxTabs ref={this.myTabs}
                // @ts-ignore

                height={560}
                >
            <div style={{ fontSize: '13px', fontFamily: 'Verdana', float: 'left' }}>

                <JqxGrid ref={this.myGrid}
                    width="240px"
                    source={this.state.source} columns={this.state.columns}

                    />

                <div style={{ marginTop: '20px' }}>
                    <div style={{ float: 'left', marginLeft: '10px' }}>
                        <JqxButton onClick={this.excelBtnOnClick}>Export</JqxButton>
                    </div>                
                </div>
            </div>

            </JqxTabs>

        );
    }
    private excelBtnOnClick() {
        this.myGrid.current!.exportdata('xls', 'jqxGrid',true);
    };


}
export default App;
