import React, { useState } from "react";
import JqxGrid, { jqx } from "jqwidgets-scripts/jqwidgets-react-tsx/jqxgrid";
import JqxInput from "jqwidgets-scripts/jqwidgets-react-tsx/jqxinput";
import "jqwidgets-scripts/jqwidgets/styles/jqx.base.css";
import "jqwidgets-scripts/jqwidgets/styles/jqx.material.css";
import { generatedata } from "./generatedata";
import JqxButton from "jqwidgets-scripts/jqwidgets-react-tsx/jqxbuttons";

const MyGrid: React.FC = () => {
  const myGrid = React.createRef<JqxGrid>();
  const [data, setUser] = useState(generatedata(100));
  const [typee,setType] = useState('array')
  const source: any = {
    datafields: [
        { name: 'firstname', type: 'string' },
        { name: 'productname', type: 'string' },
    ],
    datatype: typee,
    localdata: data

  };

  const adapter = new jqx.dataAdapter(source);

  const exportar = () => {
      //console.log('')
      myGrid.current!.exportdata('xls','archivo')

  }
  const [file, setFile] = useState();

  const fileReader = new FileReader();

  const handleOnChange = (e) => {
      setFile(e.target.files[0]);
  };

  const handleOnSubmit = (e) => {
      e.preventDefault();

      if (file) {
          fileReader.onload = function (event) {
              const csvOutput = e.target.result;
          };

          fileReader.readAsText(file);
      }
  };

  return (
      <div>
            <JqxGrid ref={myGrid}
            width="20%"
            source={adapter}
            editable={true} selectionmode={'multiplecellsadvanced'}
            columns={[
            {
                text: "First Name",
                datafield: "firstname",
                width: "100",
                columntype: 'dropdownlist'
            },
            {
                text: "Product Name",
                datafield: "productname",
                width: "100",
                columntype: 'dropdownlist'
            }
            ]}
            pageable={true}
            autoheight={true}
            sortable={true}
            theme="material"
        />
        <div style={{ float: 'left' }}>
                <JqxButton onClick={exportar}>Export to Excel</JqxButton>

            </div>
                <div style={{ float: 'left', marginLeft: '10px' }}>
                <div style={{ textAlign: "center" }}>
                <h1>excel IMPORT EXAMPLE </h1>
                <form>
                    <input
                        type={"file"}

                        accept={".xls"}
                        onChange={handleOnChange}
                    />

                    <button
                        onClick={(e) => {
                            handleOnSubmit(e);
                        }}
                    >
                        IMPORT CSV
                    </button>
                </form>
            </div>



            </div>

      </div>

  );
};

export default MyGrid;
