import * as React from 'react';

import JqxGrid, { IGridProps, jqx } from 'jqwidgets-scripts/jqwidgets-react-tsx/jqxgrid';
import { generatedata } from './generatedata';
import JqxButton from 'jqwidgets-scripts/jqwidgets-react-tsx/jqxbuttons';
class App extends React.PureComponent<{}, IGridProps> {
    private myGrid = React.createRef<JqxGrid>();
    constructor(props: {}) {        
        super(props);
        this.excelBtnOnClick = this.excelBtnOnClick.bind(this);
        const source: any = {
            datafields: [
                { name: 'firstname', type: 'string' },
                { name: 'productname', type: 'string' },
            ],
            datatype: 'array',
            localdata: generatedata(200),
            updaterow: (rowid: any, rowdata: any, commit: any): void => {
                // synchronize with the server - send update command
                // call commit with parameter true if the synchronization with the server is successful 
                // and with parameter false if the synchronization failder.
                commit(true);
            }
        };
        const dataAdapter: any = new jqx.dataAdapter(source);
        this.state = {
            columns: [
                { text: 'First Name', columntype: 'textbox', datafield: 'firstname', width: 80 },

                { text: 'Product', columntype: 'dropdownlist', datafield: 'productname', width: 195 },

            ],
            source: dataAdapter
        }
    }
    public render() {
        return (
            <div>
            <JqxGrid ref={this.myGrid}
                // @ts-ignore
                width="300px" source={this.state.source} columns={this.state.columns}
                editable={true} selectionmode={'multiplecellsadvanced'} />
            <div style={{ float: 'left' }}>
                <JqxButton onClick={this.excelBtnOnClick}>Export to Excel</JqxButton>
            </div>
            <div style={{ float: 'left', marginLeft: '10px' }}>
                        <input type='file'/>
                        <JqxButton >Import   Excel</JqxButton>


            </div>

            </div>
        );
    }
    private excelBtnOnClick() {
        this.myGrid.current!.exportdata('xls', 'jqxGrid');
    };
}
export default App;
