import * as React from 'react';



import JqxGrid, { IGridProps, jqx } from 'jqwidgets-scripts/jqwidgets-react-tsx/jqxgrid';
import JqxButton from 'jqwidgets-scripts/jqwidgets-react-tsx/jqxbuttons';


class App extends React.PureComponent<{}, IGridProps> {
    private myGrid = React.createRef<JqxGrid>();
    constructor(props: {}) {
        super(props);
        this.excelBtnOnClick = this.excelBtnOnClick.bind(this);


        const source: any = {
            datafields: [
                { name: 'ShippedDate', map: 'm\\:properties>d\\:ShippedDate', type: 'date' },
                { name: 'Freight', map: 'm\\:properties>d\\:Freight', type: 'float' },
                { name: 'ShipName', map: 'm\\:properties>d\\:ShipName', type: 'string' },
                { name: 'ShipAddress', map: 'm\\:properties>d\\:ShipAddress', type: 'string' },
                { name: 'ShipCity', map: 'm\\:properties>d\\:ShipCity', type: 'string' },
                { name: 'ShipCountry', map: 'm\\:properties>d\\:ShipCountry', type: 'string' }
            ],
            datatype: 'xml',
            id: 'm\\:properties>d\\:OrderID',
            record: 'content',
            root: 'entry',
            url: 'orders.xml'
        };

        this.state = {
            columns: [
                {
                    // update the editor's value before saving it.
                    cellvaluechanging: (row: number, column: any, columntype: any, oldvalue: any, newvalue: any): any => {
                        // return the old value, if the new value is empty.
                        if (newvalue === '') {
                            return oldvalue;
                        }
                    },
                    columntype: 'combobox',
                    createeditor: (row: number, column: any, editor: any): void => {
                        // assign a new data source to the combobox.
                        const list = ['Stuttgart', 'Rio de Janeiro', 'Strasbourg'];
                        editor.jqxComboBox({ autoDropDownHeight: true, source: list, promptText: 'Please Choose:' });
                    },
                    datafield: 'ShipCity', text: 'Ship City', width: 150
                },
                {
                    // update the editor's value before saving it.
                    cellvaluechanging: (row: number, column: any, columntype: any, oldvalue: any, newvalue: any): any => {
                        // return the old value, if the new value is empty.
                        if (newvalue === '') {
                            return oldvalue;
                        }
                    },
                    columntype: 'dropdownlist',
                    createeditor: (row: number, column: any, editor: any): void => {
                        // assign a new data source to the dropdownlist.
                        const list = ['Germany', 'Brazil', 'France'];
                        editor.jqxDropDownList({ autoDropDownHeight: true, source: list });
                    },
                    datafield: 'ShipCountry', text: 'Ship Country', width: 150
                },
                { text: 'Ship Name', datafield: 'ShipName', columntype: 'combobox' }
            ],
            source: new jqx.dataAdapter(source)
        }
    }

    private excelBtnOnClick() {
        this.myGrid.current!.exportdata('xls', 'jqxGrid');
    };


    public render() {
        return (
            <div>
            <JqxGrid ref={this.myGrid} theme={'material-purple'}
                // @ts-ignore
                source={this.state.source} columns={this.state.columns}
                pageable={false} autoheight={true} editable={true} selectionmode={'singlecell'} 

            />
                <JqxButton onClick={this.excelBtnOnClick}>Export to Excel</JqxButton>

            </div>


        );
    }
}



export default App;
