import * as React from 'react';
import './App.css';
import JqxGrid, { IGridProps, jqx } from 'jqwidgets-scripts/jqwidgets-react-tsx/jqxgrid';
import JqxTabs from 'jqwidgets-scripts/jqwidgets-react-tsx/jqxtabs';
import { generatedata } from './generatedata'
import JqxButton from 'jqwidgets-scripts/jqwidgets-react-tsx/jqxbuttons';
export interface IState extends IGridProps {
    source2: IGridProps['source'];
    columns2: IGridProps['columns']
}
class App extends React.PureComponent<{}, IState> {
    private myGrid = React.createRef<JqxGrid>();
    constructor(props: {}) {
        super(props);
        this.excelBtnOnClick = this.excelBtnOnClick.bind(this);
        const source: any = {
            datafields: [
                { name: 'Date' },
                { name: 'S&P 500' },
                { name: 'NASDAQ' }
            ],
            datatype: 'csv',
            url: 'nasdaq_vs_sp500.txt'
        };

        const source2: any = {
            datafields: [
                { name: 'firstname', type: 'string' },
                { name: 'lastname', type: 'string' },

            ],
            datatype: 'array',
            localdata: generatedata(100)
        };
        this.state = {
            columns: [
                { text: 'Date', datafield: 'Date', cellsformat: 'd', width: 250 },
                { text: 'S&P 500', datafield: 'S&P 500', width: 150 },
                { text: 'NASDAQ', datafield: 'NASDAQ' }
            ],
            columns2: [
                { text: 'First Name', datafield: 'firstname', width: 250 },
                { text: 'Last Name', datafield: 'lastname', width: 150 },

            ],
            source: new jqx.dataAdapter(source),
            source2: new jqx.dataAdapter(source2)
        }
    }
    public render() {
        return (
            <div>
            <JqxTabs width={600} height={560}>
                <ul>
                    <li style={{ marginLeft: '30px' }}>Grid 1</li>
                    <li>Grid 2</li>
                </ul>
                <div style={{ overflow: 'hidden' }}>

                    <JqxGrid width={'100%'} height={'100%'} source={this.state.source2} columns={this.state.columns2} />
                </div>
                <div style={{ overflow: 'hidden' }}>
                    <JqxGrid width={'100%'} height={'100%'} source={this.state.source} columns={this.state.columns} />                   
                </div>


            </JqxTabs>
            <JqxButton onClick={this.excelBtnOnClick}>Export to Excel</JqxButton>
            </div>
        );
    }

    private excelBtnOnClick() {
        this.myGrid.current!.exportdata('xls', 'jqxGrid');
    };

}
export default App;
